  # Define mask (inverse  of region)
  if(region == 'global') {
    mask <- NULL
  } else if(region == 'land') {
    mask <- 'sea'
  } else if(region == 'sea'){
    mask <- 'land'
  } else if(region == 'arctic'){
    mask <- 'non-arctic'
  } else if(region == 'NH-land'){
